import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Tracker from './components/Tracker';
import StoredData from './pages/StoredData';

const App: React.FC = () => {
  const [days, setDays] = useState<any[]>([]);
  const [editIdx, setEditIdx] = useState<number | null>(null);

  // Handler to edit a day
  const handleEdit = (idx: number) => setEditIdx(idx);

  // Handler to delete a day
  const handleDelete = (idx: number) => setDays(days.filter((_, i) => i !== idx));

  return (
    <Router>
      <nav style={{ margin: '1rem' }}>
        <Link to="/" style={{ marginRight: '1rem' }}>Tracker</Link>
        <Link to="/stored">Stored Data</Link>
      </nav>
      <Routes>
        <Route path="/" element={<Tracker days={days} setDays={setDays} editIdx={editIdx} setEditIdx={setEditIdx} />} />
        <Route path="/stored" element={<StoredData days={days} handleEdit={handleEdit} handleDelete={handleDelete} />} />
      </Routes>
    </Router>
  );
};

export default App;
