import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Tracker from './components/Tracker';
import StoredData from './pages/StoredData';
import Login from './pages/Login';
import SignUp from './pages/SignUp';

const App: React.FC = () => {
  const [days, setDays] = useState<any[]>([]);
  const [editIdx, setEditIdx] = useState<number | null>(null);
  const [loggedIn, setLoggedIn] = useState(false);

  // Handler to edit a day
  const handleEdit = (idx: number) => setEditIdx(idx);

  // Handler to delete a day
  const handleDelete = (idx: number) => setDays(days.filter((_, i) => i !== idx));

  return (
    <Router>
      <nav style={{ margin: '1rem' }}>
        <Link to="/" style={{ marginRight: '1rem' }}>Tracker</Link>
        <Link to="/stored" style={{ marginRight: '1rem' }}>Stored Data</Link>
        <Link to="/login" style={{ marginRight: '1rem' }}>Login</Link>
        <Link to="/signup">Sign Up</Link>
      </nav>
      <Routes>
        <Route path="/login" element={<Login onLogin={() => setLoggedIn(true)} />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/" element={<Tracker days={days} setDays={setDays} />} />
        <Route path="/stored" element={<StoredData days={days} handleEdit={handleEdit} handleDelete={handleDelete} />} />
      </Routes>
    </Router>
  );
};

export default App;
