import React, { useState } from 'react';
import './Tracker.css';

const Tracker: React.FC = () => {
  const [steps, setSteps] = useState('');
  const [calories, setCalories] = useState('');
  const [water, setWater] = useState('');
  const [workout, setWorkout] = useState('');
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [heartRate, setHeartRate] = useState('');
  const [sleep, setSleep] = useState('');
  const [stride, setStride] = useState('');

  const [days, setDays] = useState<Array<{
    steps: string;
    stride: string;
    calories: string;
    water: string;
    workout: string;
    height: string;
    weight: string;
    heartRate: string;
    sleep: string;
    bmi: string;
    distance: string;
  }>>([]);

  // Track which day is being edited (-1 means none)
  const [editIdx, setEditIdx] = useState<number | null>(null);

  const bmi =
    height && weight
      ? (parseFloat(weight) / ((parseFloat(height) / 100) ** 2)).toFixed(2)
      : '';

  const distance =
    steps && stride
      ? ((parseFloat(steps) * parseFloat(stride)) / 100000).toFixed(2)
      : '';

  const handleConfirm = () => {
    if (editIdx !== null) {
      // Edit mode: update the selected day
      const updatedDays = [...days];
      updatedDays[editIdx] = {
        steps,
        stride,
        calories,
        water,
        workout,
        height,
        weight,
        heartRate,
        sleep,
        bmi: bmi || '0',
        distance: distance || '0',
      };
      setDays(updatedDays);
      setEditIdx(null);
    } else {
      // Add mode: add new day
      setDays([
        ...days,
        {
          steps,
          stride,
          calories,
          water,
          workout,
          height,
          weight,
          heartRate,
          sleep,
          bmi: bmi || '0',
          distance: distance || '0',
        },
      ]);
    }
    // Clear fields after confirm
    setSteps('');
    setStride('');
    setCalories('');
    setWater('');
    setWorkout('');
    setHeight('');
    setWeight('');
    setHeartRate('');
    setSleep('');
  };

  // Edit handler: load selected day into form
  const handleEdit = (idx: number) => {
    const day = days[idx];
    setSteps(day.steps);
    setStride(day.stride);
    setCalories(day.calories);
    setWater(day.water);
    setWorkout(day.workout);
    setHeight(day.height);
    setWeight(day.weight);
    setHeartRate(day.heartRate);
    setSleep(day.sleep);
    setEditIdx(idx);
  };

  // Delete handler
  const handleDelete = (idx: number) => {
    setDays(days.filter((_, i) => i !== idx));
    // If deleting the day being edited, reset edit mode
    if (editIdx === idx) {
      setEditIdx(null);
      setSteps('');
      setStride('');
      setCalories('');
      setWater('');
      setWorkout('');
      setHeight('');
      setWeight('');
      setHeartRate('');
      setSleep('');
    }
  };

  return (
    <div className="tracker">
      <h2>Fitness Tracker</h2>
      <label>
        Steps:
        <input
          type="number"
          value={steps === '' ? undefined : steps}
          onChange={e => setSteps(e.target.value)}
          placeholder="Enter steps"
        />
      </label>
      <label>
        Stride Length (cm):
        <input
          type="number"
          value={stride === '' ? undefined : stride}
          onChange={e => setStride(e.target.value)}
          placeholder="Enter stride length"
        />
      </label>
      <label>
        Calories:
        <input
          type="number"
          value={calories === '' ? undefined : calories}
          onChange={e => setCalories(e.target.value)}
          placeholder="Enter calories"
        />
      </label>
      <label>
        Water Intake (L):
        <input
          type="number"
          value={water === '' ? undefined : water}
          onChange={e => setWater(e.target.value)}
          placeholder="Enter water intake"
        />
      </label>
      <label>
        Workout Time (min):
        <input
          type="number"
          value={workout === '' ? undefined : workout}
          onChange={e => setWorkout(e.target.value)}
          placeholder="Enter workout time"
        />
      </label>
      <label>
        Weight (kg):
        <input
          type="number"
          value={weight === '' ? undefined : weight}
          onChange={e => setWeight(e.target.value)}
          placeholder="Enter weight"
        />
      </label>
      <label>
        Height (cm):
        <input
          type="number"
          value={height === '' ? undefined : height}
          onChange={e => setHeight(e.target.value)}
          placeholder="Enter height"
        />
      </label>
      <label>
        Heart Rate (bpm):
        <input
          type="number"
          value={heartRate === '' ? undefined : heartRate}
          onChange={e => setHeartRate(e.target.value)}
          placeholder="Enter heart rate"
        />
      </label>
      <label>
        Sleep Hours:
        <input
          type="number"
          value={sleep === '' ? undefined : sleep}
          onChange={e => setSleep(e.target.value)}
          placeholder="Enter sleep hours"
        />
      </label>
      <button onClick={handleConfirm} style={{ marginTop: '1rem' }}>
        {editIdx !== null ? 'Update' : 'Confirm'}
      </button>

      {/* Show all stored days */}
      {days.length > 0 && (
        <div style={{ marginTop: '2rem', borderTop: '1px solid #ccc', paddingTop: '1rem' }}>
          <h3>Stored Days</h3>
          {days.map((day, idx) => (
            <div key={idx} style={{ marginBottom: '1.5rem', padding: '1rem', background: '#f9f9f9', borderRadius: '8px' }}>
              <h4>Day {idx + 1}</h4>
              <p>Steps: {day.steps || 0}</p>
              <p>Stride Length: {day.stride || 0} cm</p>
              <p>Calories: {day.calories || 0}</p>
              <p>Water Intake: {day.water || 0} L</p>
              <p>Workout Time: {day.workout || 0} min</p>
              <p>Weight: {day.weight || 0} kg</p>
              <p>Height: {day.height || 0} cm</p>
              <p>BMI: {day.bmi || 0}</p>
              <p>Distance: {day.distance || 0} km</p>
              <p>Heart Rate: {day.heartRate || 0} bpm</p>
              <p>Sleep Hours: {day.sleep || 0}</p>
              <button onClick={() => handleEdit(idx)} style={{ marginRight: '1rem' }}>Edit</button>
              <button onClick={() => handleDelete(idx)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Tracker;
